var test = require('testling'),
    CM1 = require('./lib/CM1');

test('CM1.impacts', function(t) {
  t.plan(3);

  CM1.impacts('flight', {
    origin_airport: 'IAD',
    destination_airport: 'PDX',
    airline: 'United',
    trips: 2,
    segments_per_trip: 1
  },
  function(err, impacts) {
    t.notOk(err);
    t.ok(impacts.carbon > 0);
    t.ok(impacts.methodology);
    t.end();
  });
});
